//fucntion to handle the selection sort algorithm and animation backend
export function getSelectionSortAnimation(array)
{
    //animation array to change the color of each array bar
    let animation = [];
    let auxilaryArray = array.slice();
    //calling the function to handle selection sort
    selectionSort(auxilaryArray, animation);
    return animation;

}// end of getSelectionSortAnimation(array)

//function to implement the selection sort algorithm
function selectionSort(array, animation)
{
    for(let i = 0; i < array.length - 1; i++)
    {
        let indexOfMin = i;

        for(let j = i + 1; j < array.length; j++)
        {
            //pushing element into the animation to change its color
            animation.push(["comparison1", j, indexOfMin]);
            animation.push(["comparison2", j, indexOfMin]);

            //updateing the index of the smallest element in the array with the right ones
            if(array[j] < array[indexOfMin])
            {
                indexOfMin = j
            }
        }

        animation.push("swap", indexOfMin, array[i]);
        animation.push("swap", i, array[indexOfMin]);

        //swap the new minimum element with the right element
        swap(array, indexOfMin, i);

    }
}//end of selectionSort()

//function to swap two element in array
function swap(array, firstIndex, secondIndex)
{
    let temp = array[firstIndex];
    array[firstIndex] = array[secondIndex];
    array[secondIndex] = temp;
} //end of swap()
