function quickSort(arrayt)
{
    //bound check: 
    if (left < right)
    {
        return;
    }

    //pick a pivot element in the array
    //In this case, the pivot element will be the middle element for better time complexity
    var pivot = array[(left + right) / 2];

    //recursive call until the array of both the left side and right side of the element are sorted: 
    if(array.length > 1)
    {
        //partiion the left side and right side of the pivot element:
        var index = partition(array, left,right, pivot);
        if(left < index - 1)
        {//there are still more element on the left side of the array
            quickSort(array, left, index - 1); //sort the left partition of the array
        }

        if(index  < right)
        { //more elements on the left side of the pivot
            quickSort(array, index, right); //sort the right partition of the array

        }
    }

    return array;
}

//Helper Method to partition the two sides of the array: 
function partition(array, left, right, pivot)
{
    //move the pointers along the array
    while ( left <= right)
    {
        // if the left side is lesser than the pivot, the left pointer will keep going until an element is out of place
        while(array[left] < pivot)
        {
            //keep moving right
            left++; 
        }

        //if the right side is still greater than the pivot, then the right pointer will keep going until an element is out of place
        while(array[right] > pivot)
        {
            //keep going left
            right--; 
        }

        if (left <= right)
        {
            swap(array, left, right);
            left++;
            right--;
        }
    }// end while loop

    //the pivot should (ideally) be on the left side at the end of the algorithm 
    return left;

}




//function to swap element: 
function swap(array, left, right)
{
    let temp = array[left];
    array[left] = array[right];
    array[right] = temp;

}//end of swap()
