import React from 'react';
import './SortingVisualizer.css';

//the main function of the react app
export default class SortingVisualizer extends React.Component {
    //sorting constructor:
    constructor(props) {
      super(props);

      this.state = {
        array: [],  //main array that would print out the element to be sorted.
      };
    }

    //when the component of the app is loaded, it will reset the current of element
    componentDidMount() {
      this.resetArray();
    }

    //resetting the array and generate new elements each time
    resetArray(){
        const array = [];
        //generating random integer for sorting: 
        for(let i = 0; i <= 310; i++)
        {
            //pushing the integer into the array: 
            array.push(randomIntGeneration(5, 730));

        }

        this.setState({array});

    }



    //sorting alorithms:
    mergeSort(unsortedArray)
    {
      let sortedArray = [];
      //the array must contains value inside it
      if(unsortedArray.length < 1)
      {
        alert ("Error! Array is empty!");

      }

      //if the array only contain 1 element, then no need to sort and return the array
      if(unsortedArray.length == 1)
      {
        return unsortedArray;
      }

      //To divide the array in halfs, we must find the element in the middle
      const middle = Math.floor(unsortedArray.length/2);

      //divide the array into left and right sub-arrays:
      const leftArr = unsortedArray.slice(0, middle);
      const rightArr = unsortedArray.slice(middle);
      //calls the function recursively to sort the two arrays and merge them together
      sortedArray = this.mergeArr(this.mergeSort(leftArr), this.mergeSort(rightArr));
      return sortedArray;
    } //end of mergeSort(array)

    //Helper method: 
    mergeArr(leftSide, rightSide)
    {
      let resultArray = []
      let leftIndex = 0;
      let rightIndex = 0; 

      //concatenate the two strings and sort them accordingly:
      while(leftIndex < leftSide.length && rightIndex < rightSide.length)
      {
        if (leftSide[leftIndex] < rightSide[rightIndex])
        {
          resultArray.push(leftSide[leftIndex++]);
          // //move the left cursor up:
          // leftIndex++
        }

        else{
          resultArray.push(rightSide[rightIndex++]);
          // //move the right cursor up: 
          // rightIndex++;
        }
      }
      while(leftIndex < leftSide.length) resultArray.push(leftSide[leftIndex++]);
      while(rightIndex < rightSide.length) resultArray.push(rightSide[rightIndex++]);
      //concat the two array together:
      //we perform concatenation here because there will be one element remaining in both array:
      // return resultArray.concat(leftSide.slice[leftIndex]).concat(rightSide.slice[rightIndex]);
      return resultArray;
    }
    //Sorting algorithm to implement: 
    mergeSortImpl()
    {
      const jsBuiltInSorting = this.state.array.slice().sort((a, b) => a - b); //remember to pass in the sorting method
      const mereSortAlgo = this.mergeSort(this.state.array)
      console.log(this.mergeSort(this.state.array));
      console.log(this.state.array);
      //Outputting the result: 
      console.log(checkArrayEqual(jsBuiltInSorting, mereSortAlgo));
    } //end of merge sort algorithm backend.

    quickSortImpl()
    {

    }

    heapSortImpl()
    {

    }

    bubbleSortImpl()
    {

    }

    //function to test all sorting algorithm for debugging
    testSortingAlgorithms()
    {
      //the function will create 100 array with different length
      for(var i = 0; i < 100; i++)
      {
        // create an array at each iteration: 
        const array = []
        //each array will have various length from 1 to 1000 
        for(var j = 0; j < randomIntGeneration(1, 1000); j++)
        {
          array.push(randomIntGeneration(0, 1000));

        }

      }


    }

    render()
    {
        const {array} = this.state;

        return (

          <div className="array-container">
          <>
          {array.map((value, idx) => (
            <div className="array-bar" key={idx}
              style={{
                // backgroundColor: PRIMARY_COLOR,
                height: `${value}px`,
              }}
              > 
              </div>
          ))}


          </>
           <button onClick={() => this.resetArray()}>Generate New Array</button>
           <button onClick={() => this.mergeSortImpl()}>Merge Sort</button>
           <button onClick={() => this.quickSortImpl()}>Quick Sort</button>
           <button onClick={() => this.heapSortImpl()}>Heap Sort</button>
           <button onClick={() => this.bubbleSortImpl()}>Bubble Sort</button>
           <button onClick={() => this.testSortingAlgorithms()}>
             Test Sorting Algorithms
           </button>

        </div>

        );

    }

}

//Function to generate random integer from 5 to 1000
function randomIntGeneration(min, max) 
{
  return Math.floor(Math.random() * (max - min + 1) + min);
}

//Function to test if two array of elements equal to each other
function checkArrayEqual(arr1, arr2)
{
  //if the two arrays are not equal to each other in length, then they are not equal.
  if (arr1.length !== arr2.length)
  {
    return false;
  }

  // looping through each character and check if they are both equals
  for (let i=0; i < arr1.length; i++)
  {
    if (arr1[i] !== arr2[i])
    {
      //array are equals
      return false;

    }

  }
  return true;
}
