//Function to hadnle quick sort algorithm 
export function getQuickSortAnimation(array)
{
    quickSort(array, 0, array.length - 1);
}

function quickSort(array, firstIndex, lastIndex)
{
    //bound check: 
    if (firstIndex < lastIndex)
    {
        return;
    }

    //pick a pivot element in the array
    //In this case, the pivot element will be the middle element for better time complexity
    var pivot = array[(firstIndex + lastIndex) / 2]

}
